import React, { useState } from "react";
import styled from "styled-components";
// import { db } from "../../db/firebase";
// import { setDoc, doc, collection } from "firebase/firestore";
import { useSelector } from "react-redux";
import axios from "axios";

import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import IconButton from "@mui/material/IconButton";
// import Input from "@mui/material/Input";
import FilledInput from "@mui/material/FilledInput";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import InputAdornment from "@mui/material/InputAdornment";
import FormHelperText from "@mui/material/FormHelperText";
import FormControl from "@mui/material/FormControl";
import Visibility from "@mui/icons-material/Visibility";
import VisibilityOff from "@mui/icons-material/VisibilityOff";

const AddProduct = () => {
  // const { products } = useSelector((state) => state.productsReducer);

  const [productDetails, setProductDetails] = useState({
    category: [],
  });
  const [errors, setErrors] = useState({});

  // const productsColl = collection(db, "products");

  const categories = [
    "men",
    "women",
    "electronics",
    "footwear",
    "clothing",
    "home",
  ];
  const tags = ["bestseller", "amazon_choice", "new", "false"];

  const handleFormData = (e) => {
    console.log(e);
    const { name, value, checked, type } = e.target;

    // console.log(type, value);
    if (checked && name === "category") {
      setProductDetails({
        ...productDetails,
        [name]: [...productDetails[name], value],
      });
    } else if (!checked && name === "category") {
      setProductDetails({
        ...productDetails,
        [name]: productDetails[name].filter((e) => e !== value),
      });
    } else {
      setProductDetails({
        ...productDetails,
        [name]:
          type === "checkbox" ? checked : type === "number" ? +value : value,
      });
    }
  };

  const validate = () => {
    let temp = {};
    temp.title = productDetails.title ? null : "Title is required";
    temp.image = productDetails.image ? null : "Image is required";
    temp.org_price = productDetails.org_price
      ? null
      : "Original Price is required";
    temp.disc_price = productDetails.disc_price
      ? null
      : "Discount Price is required";
    temp.qty = productDetails.qty ? null : "Quantity is required";
    temp.expected_delivery_duration = productDetails.expected_delivery_duration
      ? null
      : "Expected Delivery Duration is required";
      

    return Object.values(temp).every((el) => el === null);
  };

  const addProduct = async (e) => {
    e.preventDefault();
    validate();
    axios
      .post("http://localhost:8080/addproduct", productDetails)
      .then((data) => console.log(data))
      .catch((err) => console.log(err.message));

    // setDoc(doc(productsColl, String(products.length + 1)), productDetails, {
    //   merge: true,
    // })
    //   .then((res) => console.log("result,", res))
    //   .catch((err) => console.log("err,", err));
  };
  return (
    <Container className="border">
      <pre>{JSON.stringify(productDetails, undefined, 2)}</pre>
      <form onSubmit={addProduct}>
        <fieldset>
          {/* <FormControl> */}
          <Box
            component="form"
            sx={{
              "& .MuiTextField-root": { m: 1, width: "25ch" },
            }}
            noValidate
            autoComplete="off"
          >
            <TextField
              required
              name="title"
              value={productDetails.title}
              // error={this.state.errorText}
              // error={this.state.errorText.length === 0 ? false : true}
              placeholder="Title"
              // id="outlined-required"
              label="Required"
              variant="outlined"
              size="small"
              // helperText="Title"
              // id="outlined-helperText"
              InputLabelProps={{
                shrink: true,
              }}
              onChange={handleFormData}
            />
            <TextField
              error={false}
              // id="outlined-required"
              label="Required"
              variant="outlined"
              size="small"
              helperText="Title"
              // id="outlined-helperText"
              InputLabelProps={{
                shrink: true,
              }}
            />
            <FormControl size="small">
              <InputLabel htmlFor="outlined-adornment-amount">Price</InputLabel>
              <OutlinedInput
                id="outlined-adornment-amount"
                // value={10}
                // onChange={handleChange("amount")}
                startAdornment={
                  <InputAdornment position="start">â‚¹</InputAdornment>
                }
                label="Price"
              />
            </FormControl>
            {/* </FormControl> */}
          </Box>
          <h4>Category:</h4>
          {categories.map((cat, i) => (
            <Input key={i}>
              <input
                onInput={handleFormData}
                type="checkbox"
                name="category"
                value={cat}
              />
              {cat}
            </Input>
          ))}

          <Input>
            Coupon:
            <input onChange={handleFormData} type="number" name="coupon" />
          </Input>

          <Input>
            Delivery Duration:
            <input
              onChange={handleFormData}
              type="number"
              name="delivery_duration"
            />
          </Input>

          <Input>
            Discount Price:
            <input
              onChange={handleFormData}
              type="number"
              name="discount_price"
              step=".01"
            />
          </Input>

          <Input>
            first Delivery Free:
            <input
              onInput={handleFormData}
              type="checkbox"
              name="first_delivery_free"
              value="first_delivery_free"
            />
          </Input>

          <Input>
            Image:
            <input onChange={handleFormData} type="text" name="image" />
          </Input>
          <Input>
            Price:
            <input
              onChange={handleFormData}
              type="number"
              name="price"
              step=".01"
            />
          </Input>
          <Input>
            Prime:
            <input
              onInput={handleFormData}
              type="checkbox"
              name="prime"
              value="prime"
            />
          </Input>
          <Input>
            Quantity:
            <input onChange={handleFormData} type="number" name="qty" />
          </Input>
          <Input>
            Rating:
            <input
              onChange={handleFormData}
              type="number"
              name="rating"
              step=".1"
            />
          </Input>

          <h4>Tag:</h4>

          {tags.map((tag, i) => (
            <Input key={i}>
              <input
                onInput={handleFormData}
                type="radio"
                name="tag"
                value={tag}
              />
              {tag}
            </Input>
          ))}

          <Input>
            Title:
            <input onChange={handleFormData} type="text" name="title" />
          </Input>
          <Input>
            <input type="submit" value="submit" />
          </Input>
        </fieldset>
      </form>

      <Box
        component="form"
        sx={{
          "& > :not(style)": { m: 1, width: "25ch" },
        }}
        noValidate
        autoComplete="off"
      >
        <TextField id="filled-basic" label="Filled" variant="filled" />
        <TextField id="standard-basic" label="Standard" variant="standard" />
      </Box>
    </Container>
  );
};

export default AddProduct;

const Container = styled.div`
  padding: 10px;
`;

const Input = styled.div``;
